import React, { CSSProperties } from "react";
import "./ImageTextSplit.css";

export interface ImageTextSplitProps {
	imageSide?: "left" | "right";
	imageSrc: string;
}

class ImageTextSplit extends React.Component<ImageTextSplitProps, {}> {
	style: CSSProperties;
	imageStyle: CSSProperties;

	constructor(props: ImageTextSplitProps) {
		super(props);
		this.style = {
			flexDirection: props.imageSide == "left" ? "row" : "row-reverse",
		};
		this.imageStyle = {
			backgroundImage: `url(${props.imageSrc})`,
		}
	}

	render() {
		return (
			<div className="ImageTextSplit" style={this.style}>
				<div className="ImageTextSplit__Image">
					<img src={this.props.imageSrc} alt="" />
				</div>
				<div className="ImageTextSplit__Text">
					{this.props.children}
				</div>
			</div>
		);
	}
}

export default ImageTextSplit;
