import classNames from "classnames";
import React, { useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import "./NavBarItem.css";

export interface NavBarItemProps {
	label: string;
	page: string;
}

export interface NavBarItemState {
	path: string;
}

class NavBarItem extends React.Component<NavBarItemProps, NavBarItemState> {

	constructor(props: NavBarItemProps) {
		super(props);
		this.state = { path: window.location.pathname };
		let location = useLocation();
		useEffect(() => {
			this.onLocationChange();
		}, [location]);
	}

	active(): boolean {
		return this.state.path === this.props.page;
	}

	onLocationChange() {
		this.setState({ path: window.location.pathname });
	}

	render() {
		let active = this.active();
		return (
			<Link className={classNames("NavBarItem", { "active": active })} to={this.props.page}>
				{this.props.label}
			</Link>
		);
	}
}

export default NavBarItem;
