import React from "react";
import home_icon from "./images/home.svg";
import portfolie_icon from "./images/portfolio.svg";
import about_icon from "./images/about.svg";
import contact_icon from "./images/contact.svg";
import NavBarItem from "./NavBarItem";

export interface NavBarState {
	selected_item: number;
}

const nav_bar_items = [
	{ label: "Home", icon: home_icon },
	{ label: "Portfolie", icon: portfolie_icon },
	{ label: "About", icon: about_icon },
	{ label: "Contact", icon: contact_icon },
];

class NavBar extends React.Component<{}, NavBarState> {
	constructor(props: {}) {
		super(props);
		this.state = { selected_item: 0 };
	}
	render() {
		return (
			<div className="NavBar">
				{nav_bar_items.map((item, index) => (
					<NavBarItem label={item.label} icon={item.icon} active={index === this.state.selected_item} />
				))}
			</div>
		);
	}
}

export default NavBar;
