import React from "react";
import classNames from "classnames"

type ButtonMouseEvent = React.MouseEvent<HTMLButtonElement, MouseEvent>

export interface HamburgerButtonProps {
	opened: boolean;
	onClick: (event: ButtonMouseEvent) => void;
}

class HamburgerButton extends React.Component<HamburgerButtonProps, {}> {
	onClick(e: ButtonMouseEvent) {
		this.props.onClick(e);
	}

	render() {
		return (
			<div className={classNames("HamburgerButton", { "opened": this.props.opened, "closed": !this.props.opened })} onClick={(e) => this.onClick(e)}>
				<div className="HamburgerButton__bar bar1"></div>
				<div className="HamburgerButton__bar bar2"></div>
				<div className="HamburgerButton__bar bar3"></div>
			</div>
		);
	}
}

export default HamburgerButton;
