import React from "react";
import classNames from "classnames"

type ButtonMouseEvent = React.MouseEvent<HTMLButtonElement, MouseEvent>

export interface HamburgerButtonProps {
	open: boolean;
	onClick: (event: ButtonMouseEvent) => void;
}

class HamburgerButton extends React.Component<HamburgerButtonProps, {}> {
	onClick(e: ButtonMouseEvent) {
		this.props.onClick(e);
	}

	render() {
		return (
			<button className={classNames("HamburgerButton", { "open": this.props.open, "closed": !this.props.open })} onClick={(e) => this.onClick(e)}></button>
		);
	}
}

export default HamburgerButton;
