import React from "react";
import "./Card.css";

export interface CardProps {
	borderRadius?: string;
	width?: string;
	height?: string;
	shadowBlur?: string;
	shadowOpacity?: number;
	maxWidth?: string;
	maxHeight?: string;
	snapThreshold?: string;
}

export interface CardState {
	snap: boolean;
}

class Card extends React.Component<CardProps, CardState> {
	style: React.CSSProperties;

	constructor(props: CardProps) {
		super(props);
		this.style = {
			"borderRadius": props.borderRadius || "4px",
			"width": props.width || "95%",
			"height": props.height || "95%",
			"boxShadow": `0px 0px ${props.shadowBlur || "25px"} rgba(0, 0, 0, ${props.shadowOpacity || 0.6})`,
			"maxWidth": props.maxWidth,
			"maxHeight": props.maxHeight,
		};
		this.state = { snap: props.snapThreshold === undefined ? false : window.matchMedia(`(max-width: ${props.snapThreshold})`).matches };
	}

	componentDidMount() {
		const handler = (e: MediaQueryListEvent) => { alert(e.matches); this.setState({ snap: e.matches }) };
		window.matchMedia("(min-width: 768px)").addEventListener("change", handler);
	}

	render() {
		if (this.state.snap) {
			this.style = {
				...this.style,
				"width": "100%",
				"height": "100%",
				"borderRadius": "0px",
			}
		}
		return (
			<div className="CardWrapper">
				<div className="Card" style={this.style}>
					{this.props.children}
				</div>
			</div>
		);
	}
}

export default Card;
