	}

	onLocationChange() {
		this.setState({ path: window.location.pathname });
	}

	render() {
		let active = this.active();
		return (
			<Link className={classNames("NavBarItem", { "active": active })} to={this.props.page}>
				{this.props.label}
			</Link>
		);
	}
}
