import classNames from "classnames";
import React, { createRef, CSSProperties, RefObject } from "react";
import "./ImageTextSplit.css";

export interface ImageTextSplitProps {
	imageSide?: "left" | "right";
	imageSrc: string;
}

export interface ImageTextSplitState {
	smallScreen: boolean;
}

class ImageTextSplit extends React.Component<ImageTextSplitProps, ImageTextSplitState> {
	style: CSSProperties;
	imageStyle: CSSProperties;
	ref: RefObject<HTMLDivElement>;

	constructor(props: ImageTextSplitProps) {
		super(props);
		this.state = {
			smallScreen: false,
		};
		this.style = {
			flexDirection: props.imageSide == "left" ? "row" : "row-reverse",
		};
		this.imageStyle = {
			backgroundImage: `url(${props.imageSrc})`,
		};
		this.ref = createRef();
	}

	checkSize = () => {
		if (this.ref.current && this.ref.current.clientWidth < 600) {
			this.setState({ smallScreen: true });
		}
	}

	componentDidMount() {
		this.checkSize();
		window.addEventListener("resize", this.checkSize);
	}

	componentWillUnmount() {
		window.removeEventListener("resize", this.checkSize);
	}

	render() {
		console.log(this.state.smallScreen);
		return (
			<div className={classNames("ImageTextSplit", { "small": this.state.smallScreen })} style={this.style} ref={this.ref}>
				<div className="ImageTextSplit__Image" style={this.imageStyle} />
				<div className="ImageTextSplit__Text">
					{this.props.children}
				</div>
			</div>
		);
	}
}

export default ImageTextSplit;
