import classNames from "classnames";
import React, { createRef, CSSProperties, MutableRefObject, RefObject } from "react";
import { useResizeDetector } from "react-resize-detector";
import "./ImageTextSplit.css";

export interface ImageTextSplitProps {
	imageSide?: "left" | "right";
	imageSrc: string;
}

class ImageTextSplit extends React.Component<ImageTextSplitProps, {}> {
	style: CSSProperties;
	imageStyle: CSSProperties;
	renderedWidth?: number;
	ref: MutableRefObject<any>;

	constructor(props: ImageTextSplitProps) {
		super(props);
		this.style = {
			flexDirection: props.imageSide == "left" ? "row" : "row-reverse",
		};
		this.imageStyle = {
			backgroundImage: `url(${props.imageSrc})`,
		};
		let { width, ref } = useResizeDetector();
		this.renderedWidth = width;
		this.ref = ref;
	}

	render() {
		return (
			<div className={classNames("ImageTextSplit", { "small": this.renderedWidth && this.renderedWidth < 600 })} style={this.style} ref={this.ref}>
				<div className="ImageTextSplit__Image" style={this.imageStyle} />
				<div className="ImageTextSplit__Text">
					{this.props.children}
				</div>
			</div>
		);
	}
}

export default ImageTextSplit;
