import classNames from "classnames";
import React from "react";
import { Link } from "react-router-dom";
import "./NavBarItem.css";

export interface NavBarItemProps {
	label: string;
	page: string;
	onClick: (event: React.MouseEvent<HTMLAnchorElement, MouseEvent>) => void
}

export interface NavBarItemState {
	path: string;
}

class NavBarItem extends React.Component<NavBarItemProps, NavBarItemState> {

	constructor(props: NavBarItemProps) {
		super(props);
		this.state = { path: window.location.pathname };
	}

	active(): boolean {
		return this.state.path === this.props.page;
	}

	render() {
		return (
			<Link className={classNames("NavBarItem", { "active": this.props.active })} to={this.props.page} onClick={this.props.onClick}>
				{this.active() && <div className="NavBarItem__ActiveIndicator"></div>}
				{this.props.label}
			</Link>
		);
	}
}

export default NavBarItem;
