import coding_outdoors from "./images/coding_outdoors.jpg";
import PagePreview from "./PagePreview";

export interface AboutPreviewProps {
	imageSide?: "left" | "right";
}

export default function AboutPreview(props: AboutPreviewProps) {
	const text = `I'm a passionate software engineer, full stack web developer, and graphic designer offering ${new Date().getFullYear() - 2015} years of programming and design experience combined with an aptitude for quickly adopting new skills. I am hardworking and thorough with a passion for learning and gaining more experience in this field.`;

	return (
		<PagePreview heading="A bit about me..." imageSrc={coding_outdoors} page="/about" text={text} imageSide={props.imageSide} buttonText="About Me" />
	);
}
